mapper.delete:
delete
from hetu_tbls
where table_name = #{viewName}
  and database_id in (
    select id
    from hetu_dbs
    where catalog_name = #{catalogName}
)

mapper.update:
update hetu_tbls
set owner=#{user}
where 1 = 1
  and table_name = #{viewName}
  and database_id in (
    select id
    from hetu_dbs
    where catalog_name = #{catalogName}
)
